INSERT INTO `profi` (`id`, `name`, `photo`, `education`, `job`, `description`, `user_id`, `status`, `created_at`, `updated_at`, `deleted_at`, `specialization`, `thumbnail`) VALUES
(1, 'Викторова Олеся Ивановна', 'https://2children.ru/uploads/profi/63cffc8ecb3db.png', '<p>Высшее специальное образование</p><p>Психолог, преподаватель психологии</p><p>ГБОУ ВПО МГПУ ИПССО 2008 год</p>', '<p>Психолог в сфере образования</p>', '<p>Викторова Ольга</p><p>специалист по детскому развитию</p><p>психолог</p><p>опыт работы более 7 лет</p><p>Основные профессиональные принципы:</p>', 4, 10, 1402312307, 1402312307, NULL, NULL, NULL),
(2, 'Яремич Юлия Александровна', 'https://2children.ru/uploads/profi/63cffca250fd0.jpeg', '<p>1. Высшее. РГПУ им. Герцена (г. Санкт-Петербург), степень Бакалавра педагогики. Специализация: «Психологическое сопровождение образования лиц с проблемами в развитии», 2007-2011 г.</p><p>2. Высшее. РГПУ им. Герцена (г. Санкт-Петербург), степень Магистра по направлению «Специальное (дефектологическое) образование». Специализация: «Психолого-педагогическое сопровождение образования детей раннего возраста с проблемами в развитии», 2011-2013 г.</p>', '<p>Детский практикующий психолог, специалист по раннему вмешательству, коррекционный педагог, дефектолог.</p><p>Опыт работы педагогом-психологом высшей категории в детских садах города Санкт-Петербурга-11 лет.</p>', '<p>Интересуюсь всем, что связано с детьми. Знание детской психологии помогает найти ключик к каждому ребенку, помочь ему вырасти счастливым и уверенным в себе. Как мама, знаю не понаслышке, с какими трудностями сталкиваются современные родители при воспитании детей. Поэтому мои рекомендации всегда простые, проверенные на собственном опыте и эффективные.</p><p>Контакты: yulias-er@yandex.ru</p>', 5, 10, 1402312308, 1402312308, NULL, NULL, NULL);
